mapper.select:
select d.*
from crm_company d

	d.status = 0

		and d.crm_id = #{crmId}


		and d.name like concat('%',#{name},'%')


		and d.salesman_name = #{salesmanName}



	limit #{limit} offset #{offset}

mapper.update:
update crm_company
status = #{status}, code = #{code}, crm_id = #{crmId}, unit = #{unit}, name = #{name}, address = #{address}, phone = #{phone}, salesman_name = #{salesmanName}, salesman_phone = #{salesmanPhone}, update_time = #{updateTime}, done = #{done}
where id = #{id}
